func Test_parseDer(t *testing.T) {
	var testSig = "3045022100af5778b81ae8817c6ae29fad8c1113d501e521c885a65c2c4d71763c4963984b022020687b73f5c90243dc16c99427d6593a711c52c8bf09ca6331cdd42c66edee74"
	sig := common.FromHex(testSig)
	t.Log("size sig:", len(sig))
	r, s, err := paraseDERCode(sig)
	if err != nil {
		t.Log("err", err)
		return
	}
	t.Log("r:", common.Bytes2Hex(r), "size:", len(r))
	jmstr, _ := json.Marshal(big.NewInt(1).SetBytes(r))
	t.Log("jmstr", string(jmstr))
	t.Log("s:", common.Bytes2Hex(s), "size:", len(s))
}

func Test_checkSig(t *testing.T) {
	var word = "hello"
	c, err := crypto.Load("secp256k1", -1)
	if err != nil {
		t.Log("err", err)
		return
	}
	var key [32]byte
	rd := rand.New(rand.NewSource(time.Now().UnixNano()))
	rd.Read(key[:])
	signKey, err := c.PrivKeyFromBytes(key[:])
	if err != nil {
		t.Log("err:", err)
		return
	}

	sigbytes := signKey.Sign([]byte(word)).Bytes()
	t.Log("hexSig:", common.Bytes2Hex(sigbytes))
	pr, ps, _ := paraseDERCode(sigbytes)
	t.Log("pr:", common.Bytes2Hex(pr))
	t.Log("ps:", common.Bytes2Hex(ps))
	cfg := ctypes.NewChain33Config(ctypes.GetDefaultCfgstring())
	eipSigner := etypes.NewEIP155Signer(big.NewInt(int64(cfg.GetChainID())))
	r, s, v, _ := makeDERSigToRSV(eipSigner, sigbytes)
	t.Log("r:", common.Bytes2Hex(r.Bytes()))
	t.Log("s:", common.Bytes2Hex(s.Bytes()))
	t.Log("v:", common.Bytes2Hex(v.Bytes()))
	sig2 := makeDERsignature(r.Bytes(), s.Bytes())
	t.Log("sig2:", common.Bytes2Hex(sig2))
	assert.Equal(t, sig2, sigbytes)

}

func Test_BlockDetailToEthBlock(t *testing.T) {
	cfg := ctypes.NewChain33Config(ctypes.GetDefaultCfgstring())
	hexblock := ""
	var blockdetails = new(ctypes.BlockDetails)
	err := ctypes.Decode(common.FromHex(hexblock), blockdetails)
	if err != nil {
		t.Log("err", err)
		return
	}
	blocks, err := BlockDetailToEthBlock(blockdetails, cfg, true)
	if err != nil {
		t.Log("err", err)
		return
	}

	hexblocks, _ := json.MarshalIndent(blocks, " ", "\t")
	t.Log("blockjstr:", string(hexblocks))

}
func Test_TxDetailsToEthTx(t *testing.T) {
	cfg := ctypes.NewChain33Config(ctypes.GetDefaultCfgstring())
	hexTxs := ""
	var details ctypes.TransactionDetails
	ctypes.Decode(common.FromHex(hexTxs), &details)
	txs, receipts, err := TxDetailsToEthTx(&details, cfg)
	if err != nil {
		t.Log("err:", err)
		return
	}
	jtxs, _ := json.MarshalIndent(txs, " ", "\t")
	t.Log("json txs:", string(jtxs))
	jreceipts, _ := json.MarshalIndent(receipts, " ", "\t")
	t.Log("json receipts:", string(jreceipts))
	assert.Equal(t, txs[0].From, "1N2aNfWXqGe9kWcL8u9TYpj5RzVQbjwKAP")
	assert.Equal(t, txs[0].Data, "0x7750c9f0000000000000000000000000e6a6ea8e06bad72af51c04e1ee930b40cc4c4512000000000000000000000000096cf8ba17671e7aa4f76fb81813a6d713ba866b00000000000000000000000000000000000000000000000000005b21a4630080")
}
