package btc

import (
	"bytes"
	"encoding/hex"
	"testing"
)

func TestMultisigFromScript(t *testing.T) {
	txt := ""
	d, _ := hex.DecodeString(txt)
	s, e := NewMultiSigFromScript(d)
	if e != nil {
		t.Error(e.Error())
	}

	b := s.Bytes()
	if !bytes.Equal(b, d) {
		t.Error("Multisig script does not match the input\n", hex.EncodeToString(b), "\n", hex.EncodeToString(d))
	}
}
